" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Classification of scleroderma disorders",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
