var title_f43_3_44091="Clinical features of Laron syndrome";
var content_f43_3_44091=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F70163&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F70163&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical features of primary growth hormone insensitivity (Laron syndrome)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Growth and development",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Birth weight and length near normal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Postnatal growth failure, moderate to severe",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bone age delayed relative to chronological age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Short limb length in relation to trunk length",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Puberty delayed 3 to 7 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal sexual function and fertility",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intellectual development normal or modestly impaired",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Head and neck",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Face small in relation to head circumference",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Delayed dentition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Blue sclerae",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Metabolic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypoglycemia, especially in infants and children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperlipidemia and variable degrees of insulin resistance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Osteopenia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obesity",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br>",
"      <ol>",
"       <li>",
"        Reiter EO, Rosenfeld RG. Growth retardation in: Kronenberg HM et al, Eds. Williams Textbook of Endocrinology, 11th Ed. Saunders Elsevier, Philadelphia, 2008. p.903.",
"       </li>",
"       <li>",
"        Laron Z. Laron syndrome (primary growth hormone resistance or insensitivity): the personal experience 1958-2003. J Clin Endocrinol Metab 2004; 89:1031.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
