var title_f43_3_44092="Management sexual dysfunction";
var content_f43_3_44092=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F71511&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F71511&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Second Princeton consensus panel recommendations for risk assignment for sexual activity and management of sexual dysfunction",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Grade of risk",
"       </td>",
"       <td class=\"subtitle1\">",
"        Criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        CAVEATS",
"       </td>",
"       <td>",
"        Phosphodiesterase-5 inhibitors contraindicated with any nitrate preparation should not be taken with alpha-blocking agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Nitrates should not be administered within 24 hours (or longer in patients with renal or hepatic dysfunction) of sildenafil, 24 hours of vardenafil, or up to 48 hours of tadalafil",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"9\">",
"        Low risk",
"       </td>",
"       <td>",
"        No symptoms and less than three major cardiovascular risk factors (excluding gender)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Controlled hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mild, stable angina",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Post-successful coronary revascularization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Uncomplicated MI more than six to eight weeks previously in patients who do not have exercise-induced ischemia or who have undergone coronary revascularization",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mild valvular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Asymptomatic LV dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Patients at low risk can safely initiate or resume sexual activity and can be treated for sexual dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"7\">",
"        Intermediate or indeterminate risk",
"       </td>",
"       <td>",
"        No symptoms and three or more major cardiovascular risk factors (excluding gender)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Moderate, stable angina",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recent MI (more than two but less than six weeks previously); in patients who have not undergone revascularization, the risk can be assessed with stress testing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Asymptomatic LV dysfunction with LV ejection fraction &lt;40 percent or NYHA class II heart failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Noncardiac sequelae of atherosclerotic disease, such as peripheral vascular disease or prior stroke or transient ischemic attack",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Patients at intermediate or indeterminate risk should undergo further cardiologic evaluation, such as stress testing (particularly in patients with a sedentary lifestyle), in an attempt to restratify the patient into the high risk or low risk category",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"9\">",
"        High risk",
"       </td>",
"       <td>",
"        Unstable or refractory angina",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Uncontrolled hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        NYHA class III/IV heart failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        MI within the past two weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High-risk arrhythmias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obstructive hypertrophic cardiomyopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Moderate to severe valvular disease, particular aortic stenosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Management",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Patients at high risk should be stabilized by appropriate therapy before resuming sexual activity",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     MI: myocardial infarction; LV: left ventricular; NYHA: New York Heart Association.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Kostis B, Jackson G, Rosen R, et al. Am J Cardiol 2005; 96:313.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
