var title_f43_31_44540="Requirements for patient and lesion selection";
var content_f43_31_44540=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F73459&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F73459&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACCF/AHA/SCAI expert consensus document requirements for patient and lesion selection",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist_other_start\">",
"        <strong>",
"         Patient risk:",
"        </strong>",
"        expected clinical risk in case of occlusion caused by procedure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         High patient risk:",
"        </strong>",
"        Patients with any of the following:",
"        <br/>",
"        <ul>",
"         <li>",
"          Decompensated congestive heart failure (Killip Class 3) without evidence for active ischemia, recent CVA, advanced malignancy, known clotting disorders",
"         </li>",
"         <li>",
"          LVEF &lt;25 percent",
"         </li>",
"         <li>",
"          Left main stenosis (&ge;50 percent diameter) or 3-vessel disease unprotected by prior bypass surgery (&gt;70 percent stenoses in the proximal segment of all major epicardial coronary arteries)",
"         </li>",
"         <li>",
"          Single-target lesion that jeopardizes &gt;50 percent of remaining viable myocardium",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other_start\">",
"        <strong>",
"         Lesion risk:",
"        </strong>",
"        probability that procedure will cause acute vessel occlusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Increased lesion risk:",
"        </strong>",
"        lesions in open vessels with any of the following characteristics:",
"        <br/>",
"        <ul>",
"         <li>",
"          Diffuse disease (&gt;2 cm in length) and excessive tortuosity of proximal segments",
"         </li>",
"         <li>",
"          More than moderate calcification of a stenosis or proximal segment",
"         </li>",
"         <li>",
"          Location in an extremely angulated segment (&gt;90 percent)",
"         </li>",
"         <li>",
"          Inability to protect major side branches",
"         </li>",
"         <li>",
"          Degenerated older vein grafts with friable lesions",
"         </li>",
"         <li>",
"          Substantial thrombus in the vessel or at the lesion site",
"         </li>",
"         <li>",
"          Any other feature that may, in the operator's judgment, impede successful stent deployment",
"         </li>",
"        </ul>",
"        Aggressive measures to open CTOs are also discouraged because of an increased risk of perforation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other_start\">",
"        <strong>",
"         Strategy for surgical backup based on lesion and patient risk:",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <ul>",
"         <li>",
"          <strong>",
"           High-risk patients with high-risk lesions",
"          </strong>",
"          should not undergo nonemergency PCI at a facility without on-site surgery",
"         </li>",
"         <li>",
"          <strong>",
"           High-risk patients with non-high-risk lesions:",
"          </strong>",
"          Nonemergency patients with this profile may undergo PCI, but confirmation that a cardiac surgeon and operating room are immediately available is necessary",
"         </li>",
"         <li>",
"          <strong>",
"           Non-high-risk patients with high-risk lesions",
"          </strong>",
"          require no additional precautions",
"         </li>",
"         <li>",
"          <strong>",
"           Non-high-risk patients with non-high-risk lesions",
"          </strong>",
"          require no additional precautions. Best scenario for PCI without on-site surgery.",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CTO: chronic total occlusion; CVA: cerebrovascular accident; LVEF: left ventricular ejection fraction; PCI: percutaneous coronary intervention; SCAI: Society for Cardiovascular Angiography and Interventions.",
"    </div>",
"    <div class=\"reference\">",
"     From: Levine GN, Bates ER, Blankenship JC, et al. 2011 ACCF/AHA/SCAI Guideline for Percutaneous Coronary Intervention A Report of the American College of Cardiology Foundation/American Heart Association Task Force on Practice Guidelines and the Society for Cardiovascular Angiography and Interventions. J Am Coll Cardiol 2011; 58:e44. Table used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
