" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Systemic chemotherapy for metastatic colorectal cancer: Completed clinical trials",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?43/32/44554/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?43/32/44554/contributors\">",
"     Jeffrey W Clark, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?43/32/44554/contributors\">",
"     Axel Grothey, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?43/32/44554/contributors\">",
"     Section Editor",
"    </a>",
