var title_f43_32_44556="Measurement of BP";
var content_f43_32_44556=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F65136&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F65136&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for the measurement of blood pressure to diagnose and treat hypertension",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Patient conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Posture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Initially, check for postural changes by taking readings after five minutes supine, then immediately and two minutes after standing; this is particularly important in patients over age 65 years, diabetics, or those taking antihypertensive drugs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sitting pressures are recommended for routine follow-up; the patient should sit quietly with the back supported for five minutes and the arm supported at the level of the heart",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Circumstances",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        No caffeine during the hour preceding the reading, and no smoking during the preceding 30 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        No exogenous adrenergic stimulants, such as phenylephrine in decongestants or eye drops for pupillary dilatation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        A quiet, warm setting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Home readings should be taken upon varying circumstances",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Equipment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Cuff size",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        The length of the bladder should be 80 percent, and the width of the bladder should be at least 40 percent of the circumference of the upper arm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Manometer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Aneroid gauges should be calibrated every six months against a mercury manometer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Technique",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Number of readings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Take at least two readings on each visit, separated by as much time as possible; if readings vary by more than 5 mmHg, take additional reading until two consecutive readings are close",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        For the diagnosis of hypertension, take three readings at least one week apart",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Initially, take blood pressure in both arms; if pressures differ, use the higher arm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        If the arm pressure is elevated, take the pressure in one leg, particularly in patients under age 30 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Performance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Inflate the bladder quickly to 20 mmHg above the systolic pressure as estimated from loss of radial pulse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Deflate the bladder 3 mmHg per second",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Record the Korotkoff phase V (disappearance) as the diastolic pressure except in children in whom use of phase IV (muffling) may be preferable",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        If the Korotkoff sounds are weak, have the patient raise the arm, open and close the hand five to ten times, and then inflate the bladder quickly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Recordings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Note the pressure, patient position, arm, and cuff size: eg, 140/90, seated, right arm, large adult cuff",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
