" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of low-grade glioma",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?43/33/44568/contributors\">",
