var title_f43_33_44572="Suicide evaluation";
var content_f43_33_44572=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F63368&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F63368&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    \"MALPRACTICE\" mnemonic for evaluation of suicidal child or adolescent",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"1\" rowspan=\"3\">",
"        M -Mental health",
"       </td>",
"       <td>",
"        Does the patient have a psychiatric diagnosis?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Is he or she taking psychotropic medications?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Is there a family history of mental health illness?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"1\" rowspan=\"3\">",
"        A -Attempts",
"       </td>",
"       <td>",
"        When did the patient first consider suicide?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Has the patient tried to harm him- or herself before?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Did he or she receive medical attention for that attempt?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"1\" rowspan=\"3\">",
"        L -Lethality",
"       </td>",
"       <td>",
"        Did the patient want to die?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Does he or she still want to?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Does he or she have access to lethal means?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"1\" rowspan=\"3\">",
"        P -Plans",
"       </td>",
"       <td>",
"        Is the patient able to make plans for the future?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Will he or she graduate from school?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        What kind of job will he or she do?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"1\" rowspan=\"2\">",
"        R -Risk-taking",
"       </td>",
"       <td>",
"        Is the patient engaging in activities that may be disguised attempts at self-harm?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Is the patient engaging in unprotected sexual activity?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"1\" rowspan=\"4\">",
"        A -Alcohol and drugs",
"       </td>",
"       <td>",
"        Were substances involved in the current attempt?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For how long have substances been used?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        What substances are being used?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        When was the last use?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        C -Conflict",
"       </td>",
"       <td>",
"        Is there an interpersonal conflict that precipitated this event?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"1\" rowspan=\"3\">",
"        T -Trauma",
"       </td>",
"       <td>",
"        Does the patient report having been physically or sexually abused?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Has the patient been a witness to violence?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Has the patient recently lost a loved one?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"1\" rowspan=\"4\">",
"        I -Impulsivity",
"       </td>",
"       <td>",
"        Was the act premeditated?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        How much planning went into the suicide attempt?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Was a suicide note written?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Did the patient tell anyone about his or her plans?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\">",
"        C -Community resources",
"       </td>",
"       <td>",
"        Does the patient have a social support system?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_left\" colspan=\"1\" rowspan=\"3\">",
"        E -Exposure",
"       </td>",
"       <td>",
"        Have other family members or friends attempted or completed suicide?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Has the patient been exposed to suicide in the media?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Is this attempt part of a suicide epidemic?",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Press, BR, Khan, SA. Curr Opin Pediatr 1997; 9:237.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
