var title_f43_39_44667="Risk factors for preeclampsia";
var content_f43_39_44667=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F61266&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F61266&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Factors associated with an increased risk of developing preeclampsia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Nulliparity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Preeclampsia in a previous pregnancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Age &gt;40 years or &lt;18 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Family history of preeclampsia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic renal disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Antiphospholipid antibody syndrome or inherited thrombophilia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vascular or connective tissue disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diabetes mellitus (pregestational and gestational)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Multifetal gestation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        High body mass index",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;Black race",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Male partner whose mother or previous partner had preeclampsia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hydrops fetalis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unexplained fetal growth restriction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Woman herself was small for gestational age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fetal growth restriction, abruptio placentae, or fetal demise in a previous pregnancy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prolonged interpregnancy interval",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"font-family: verdana,sans-serif; font-size: 10pt;\">",
"         Partner related factors (new partner,",
"         <span style=\"font-size: 13px;\">",
"          limted sperm exposure [eg,",
"         </span>",
"         previous use of barrier contraception])",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hydatidiform mole&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Susceptibility genes&nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    By comparison, smoking decreases the risk of preeclampsia",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
