var script_f43_42_44705=[" ",
"",
"var calctxt = '';",
"var xmltxt = '';",
"var htmtxt = '';",
"",
"",
"function CommunityAcqPneumonia_fx() {",
"with(document.CommunityAcqPneumonia_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"if (cc1[0].checked){",
"Score = Score + 0;",
"}",
"if (cc1[1].checked){",
"Score = Score + -10;",
"}",
"thisvalue = parseFloat(cc2.value);",
"if (isNaN(thisvalue)) thisvalue = 0;",
"ThisScore =  thisvalue * 1 + 0;",
"Score =  Score + ThisScore;",
"if (cc3.checked){",
"Score = Score + 10;",
"}",
"if (cc4.checked){",
"Score = Score + 30;",
"}",
"if (cc5.checked){",
"Score = Score + 20;",
"}",
"if (cc6.checked){",
"Score = Score + 10;",
"}",
"if (cc7.checked){",
"Score = Score + 10;",
"}",
"if (cc8.checked){",
"Score = Score + 10;",
"}",
"if (cc9.checked){",
"Score = Score + 20;",
"}",
"if (cc10.checked){",
"Score = Score + 20;",
"}",
"if (cc11.checked){",
"Score = Score + 20;",
"}",
"if (cc12.checked){",
"Score = Score + 15;",
"}",
"if (cc13.checked){",
"Score = Score + 10;",
"}",
"if (cc14.checked){",
"Score = Score + 30;",
"}",
"if (cc15.checked){",
"Score = Score + 20;",
"}",
"if (cc16.checked){",
"Score = Score + 20;",
"}",
"if (cc17.checked){",
"Score = Score + 10;",
"}",
"if (cc18.checked){",
"Score = Score + 10;",
"}",
"if (cc19.checked){",
"Score = Score + 10;",
"}",
"if (cc20.checked){",
"Score = Score + 10;",
"}",
"cctotal.value = Score;",
"",
"if (doCalc){",
"rrclr();",
"if ((Score >= 0) && (Score <= 50)){ document.getElementById('rr1_1').bgColor = '#cccccc';",
"}",
"if ((Score >= 51) && (Score <= 70)){ document.getElementById('rr1_2').bgColor = '#cccccc';",
"}",
"if ((Score >= 71) && (Score <= 90)){ document.getElementById('rr1_3').bgColor = '#cccccc';",
"}",
"if ((Score >= 91) && (Score <= 130)){ document.getElementById('rr1_4').bgColor = '#cccccc';",
"}",
"if ((Score >= 131) && (Score <= 395)){ document.getElementById('rr1_5').bgColor = '#cccccc';",
"}",
"",
"}",
"}",
"}",
"",
"",
"",
"",
"function rrclr(){",
"document.getElementById('rr1_1').bgColor = \"\";",
"document.getElementById('rr1_2').bgColor = \"\";",
"document.getElementById('rr1_3').bgColor = \"\";",
"document.getElementById('rr1_4').bgColor = \"\";",
"document.getElementById('rr1_5').bgColor = \"\";",
"}",
"",
"",
""].join("\n");
