var title_f43_43_44732="Types of vasculitis PI";
var content_f43_43_44732=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F85669&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F85669&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Types of vasculitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type of vasculitis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Affected body part",
"       </td>",
"       <td class=\"subtitle1\">",
"        People often affected",
"       </td>",
"       <td class=\"subtitle1\">",
"        Symptoms can include",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Takayasu arteritis",
"       </td>",
"       <td>",
"        Aorta - the main artery that gets blood from the heart",
"       </td>",
"       <td>",
"        Women between 10 and 40. Not common in the United States.",
"       </td>",
"       <td>",
"        Pain and weakness in arms or legs, belly pain after eating",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Giant cell arteritis",
"       </td>",
"       <td>",
"        Aorta, arteries on face and head",
"       </td>",
"       <td>",
"        Older people and people with a condition called \"polymyalgia rheumatica\"",
"       </td>",
"       <td>",
"        Headache, pain in the jaw, trouble seeing clearly or seeing from 1 eye",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Polyarteritis nodosa",
"       </td>",
"       <td>",
"        Kidneys, skin, other body parts",
"       </td>",
"       <td>",
"        People with lupus, rheumatoid arthritis, hepatitis B or C, HIV (human immunodeficiency virus, the virus that causes AIDS), or a cancer called \"hairy cell leukemia\"",
"       </td>",
"       <td>",
"        Tiredness, weight loss, fever, red bumps or purple bumps on skin, numbness or tingling in hands or feet, belly pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Kawasaki disease",
"       </td>",
"       <td>",
"        Heart, other body parts",
"       </td>",
"       <td>",
"        Children under 5 years old",
"       </td>",
"       <td>",
"        Fever, swelling, and redness in areas with inflammation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Primary central nervous system vasculitis",
"       </td>",
"       <td>",
"        Brain and spinal cord",
"       </td>",
"       <td>",
"        Middle-aged adults",
"       </td>",
"       <td>",
"        Headache, confusion, stroke",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Churg-Strauss vasculitis",
"       </td>",
"       <td>",
"        Lungs, nose and sinuses, skin, other body parts",
"       </td>",
"       <td>",
"        People with asthma",
"       </td>",
"       <td>",
"        Asthma, allergies, sores on the skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Granulomatosis with polyangiitis (Wegener's)",
"       </td>",
"       <td>",
"        Nose, sinuses, lungs, kidneys",
"       </td>",
"       <td>",
"        Older adults",
"       </td>",
"       <td>",
"        Fever, joint pain, weight loss, runny or \"crusty\" nose, ear infections, cough, brown or dark urine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Henoch-Sch&ouml;nlein purpura",
"       </td>",
"       <td>",
"        Skin, kidneys",
"       </td>",
"       <td>",
"        Children between age 3 and 15, but can affect adults",
"       </td>",
"       <td>",
"        Belly and joint pain, skin rash, brown or dark urine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cryoglobulinemia",
"       </td>",
"       <td>",
"        Blood",
"       </td>",
"       <td>",
"        People infected with hepatitis C",
"       </td>",
"       <td>",
"        Red or purple skin bumps, tiredness, joint and muscle pain, swollen glands",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypersensitivity vasculitis",
"       </td>",
"       <td>",
"        Skin",
"       </td>",
"       <td>",
"        People taking medicines that cause a reaction",
"       </td>",
"       <td>",
"        Red or purple skin bumps or other rash",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Beh&ccedil;et's disease",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Adults age 20 to 40",
"       </td>",
"       <td>",
"        Sores in mouth or on skin or genitals, eye problems, joint pain and swelling",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Vasculitis is inflammation in the blood vessels. There are many different types that affect different parts of the body. Some types are more serious than others.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
