" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Paget disease of the breast",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?0/0/14/contributors\">",
