" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Chylopericardium and cholesterol pericarditis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?5/0/5126/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?5/0/5126/contributors\">",
"     Brian D Hoit, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?5/0/5126/contributors\">",
"     Section Editor",
