" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of heart failure in infants and children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?5/16/5386/contributors\">",
