var title_f5_17_5403="Corticosteroid coverage surgery";
var content_f5_17_5403=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F69479&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=0\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F69479&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=0\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Corticosteroid coverage for surgery in patients taking exogenous corticosteroids",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        For minor procedures or surgery under local anesthesia (eg, inguinal hernia repair) take usual morning steroid dose. No extra supplementation is necessary.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For moderate surgical stress (eg, lower extremity revascularization, total joint replacement) take usual morning steroid dose. Give 50 mg hydrocortisone intravenously just before the procedure and 25 mg of hydrocortisone every 8 hours for 24 hours. Resume usual dose thereafter.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For major surgical stress (eg, esophagogastrectomy, total proctocolectomy, open heart surgery) take usual&nbsp;morning steroid dose. Give 100 mg of intravenous hydrocortisone before induction of anesthesia, and 50 mg every 8 hours for 24 hours. Taper dose by half per day to maintenance level.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
