"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Cause",
"       </td>",
"       <td class=\"subtitle1\">",
"        Setting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obesity",
"       </td>",
"       <td>",
"        Particularly likely when leg is large and suspected effusion is small",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thick medial trianglular fat or medial plica",
"       </td>",
"       <td>",
"        Likely when lateral approach is successful after medial approach fails",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lipoma arborescens or gelatinous \"pseudoeffusion\"",
"       </td>",
"       <td>",
"        Likely in particularly large and chronic inflammatory effusions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Obstruction of needle lumen by particular matter",
"       </td>",
"       <td>",
"        Likely in rheumatoid effusion",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
