var title_f5_2_5163="Hay Ison criteria for diagnosis of bacterial vaginosis";
var content_f5_2_5163=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F86620&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F86620&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Hay Ison criteria for diagnosis of bacterial vaginosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Grade 0: No bacteria present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grade 1: Lactobacillus morphotypes predominate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grade 2: Mixed flora with some lactobacilli present, but gardnerella or mobiluncus morphotypes also present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grade 3*: Predominantly gardnerella and/or mobiluncus morphotypes. Few or absent lactobacilli.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Grade 4: Gram-positive cocci predominate",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     *Grade 3 is diagnostic of bacterial vaginosis.",
"    </div>",
"    <div class=\"reference\">",
"     Source: Ison CA, Hay PE. Validation of a simplified grading of Gram stained vaginal smears for use in genitourinary medicine clinics. Sex Transm Infect 2002; 78:413.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
