var outline_f5_2_5164=null;
var title_f5_2_5165="Echinocandins PK";
var content_f5_2_5165=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F56212&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F56212&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Selected pharmacokinetic characteristics of the echinocandins in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Variable",
"       </td>",
"       <td class=\"subtitle1\">",
"        Caspofungin",
"       </td>",
"       <td class=\"subtitle1\">",
"        Micafungin",
"       </td>",
"       <td class=\"subtitle1\">",
"        Anidulafungin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Elimination half-life adult* (hours)",
"       </td>",
"       <td>",
"        27-50",
"       </td>",
"       <td>",
"        14-17.2",
"       </td>",
"       <td>",
"        40-50",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Metabolism",
"       </td>",
"       <td>",
"        Hepatic: slow peptide hydrolysis and N-acetylation, with some spontaneous degradation to peptide product",
"       </td>",
"       <td>",
"        Hepatic: catechol-O-methytransferase (COMT) and to a lesser extent, CYP1A2, 2B6, 2C and 3A4",
"       </td>",
"       <td>",
"        Not metabolized: undergoes slow spontaneous degradation to inactive metabolites",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dosage adjustment for hepatic insufficiency",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Mild (Child-Pugh 5-6): none",
"         </li>",
"         <li>",
"          Moderate (Child-Pugh 7-9): decrease maintenance dose to 35 mg/day",
"         </li>",
"         <li>",
"          Severe (Child-Pugh &gt;9): no data",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Mild and Moderate (Child-Pugh 5-9): none",
"         </li>",
"         <li>",
"          Severe (Child-Pugh &gt;9): no data",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        Not necessary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dosage adjustment for renal insufficiency",
"       </td>",
"       <td>",
"        Not necessary",
"       </td>",
"       <td>",
"        Not necessary",
"       </td>",
"       <td>",
"        Not necessary",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Drug interactions",
"       </td>",
"       <td>",
"        <ul>",
"         <li>",
"          Cyclosporine increases caspofungin AUC by ~35 percent",
"         </li>",
"         <li>",
"          Potent inducers of metabolism (eg, rifampin, efavirenz, nevirapine, phenytoin, dexamethasone, carbemazepine) reduce caspofungin AUC by 15-30 percent",
"         </li>",
"         <li>",
"          Caspofungin reduces tacrolimus AUC by ~20 percent",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        N/A",
"       </td>",
"       <td>",
"        N/A",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        CSF penetration (percent of plasma)",
"       </td>",
"       <td>",
"        &lt;0.1",
"       </td>",
"       <td>",
"        &lt;0.1",
"       </td>",
"       <td>",
"        &lt;0.1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fraction urine excretion (percent)",
"       </td>",
"       <td>",
"        1.4",
"       </td>",
"       <td>",
"        0.7",
"       </td>",
"       <td>",
"        &lt;1",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Protein binding (percent)",
"       </td>",
"       <td>",
"        96-97",
"       </td>",
"       <td>",
"        99.8",
"       </td>",
"       <td>",
"        84",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vd (L/kg)",
"       </td>",
"       <td>",
"        0.14",
"       </td>",
"       <td>",
"        0.22-0.24",
"       </td>",
"       <td>",
"        0.50",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AUC: area under curve; CYP: cytochrome P; N/A: not applicable; CSF: cerebrospinal fluid; Vd: volume of distribution.",
"     <br>",
"      * Half-life of caspofungin and micafungin may be reduced in young children and infants compared with adults due to more rapid clearance.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Sources:",
"     <br>",
"      <ol>",
"       <li>",
"        US FDA-approved product information.",
"       </li>",
"       <li>",
"        Stone, JA, Holland, SD, Wickersham, PJ, et al. Single- and multiple-dose pharmacokinetics of caspofungin in healthy men. Antimicrob Agents Chemother 2002; 46:739.",
"       </li>",
"       <li>",
"        Stone, JA, Xu, X, Winchell, GA, et al. Disposition of caspofungin: role of distribution in determining pharmacokinetics in plasma. Antimicrob Agents Chemother 2004; 48:815.",
"       </li>",
"       <li>",
"        Dodds Ashley, E, Lewis, R, Lewis, J, et al. Pharmacology of systemic antifungal agents. Clin Infect Dis 2006; 43 Suppl 1:S28.",
"       </li>",
"       <li>",
"        Sucher, AJ, Chahine, EB, Balcer, HE. Echinocandins: the newest class of antifungals. Ann Pharmacotherapy 2009; 43:1647.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
