var title_f5_22_5485="Ethics living donor transplant";
var content_f5_22_5485=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F65453&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F65453&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    American Society of Transplant Surgeons Ethics Committee position paper on living donor liver transplantation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Donor selection and evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Potential donors should be healthy individuals who are carefully",
"evaluated and approved by a multidisciplinary team, including",
"hepatologists and surgeons, to ensure that they can tolerate the",
"procedure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Potential donors should undergo evaluation to ensure that they fully understand the procedure and associated risks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Potential donors must be of legal age and have sufficient intellectual",
"ability to understand the procedure and give informed consent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Potential donors who are believed or known to be coerced must be excluded",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Potential donors need to have the ability and willingness to comply with long-term follow-up",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Recipient criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recipients need to be medically suitable for liver transplantation by standard criteria of the transplant center",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recipients need to understand and accept that the donation will put the donor at significant risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Although it may not be possible to firmly state",
"that adult-to-adult living donor liver transplantation should not be",
"performed in situations in which the recipient has a poor chance of",
"survival, the added risk to the donor must be balanced with a realistic",
"estimate of the chances of success.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Center criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Centers should undergo careful institutional planning that shows the following:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Consideration of the significant risk of the procedure to the donor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Establishment of an appropriate informed consent process",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Surgical expertise in liver transplantation and hepatobiliary surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        An",
"existing need for living donation versus cadaveric donation, shown by",
"insufficient cadaver organs for transplantation resulting in",
"potentially avoidable deaths on the waiting list",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adequate resources, eg, multiple surgical teams, adequate operating room resources, and institutional support",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ongoing oversight",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Consent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Informed consent for the procedure should contain the following elements:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        The risk for death during or after the operation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        The risk for liver failure resulting in the need for transplantation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        The risk for life-threatening infection resulting from the operation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        The risk for blood-borne infection acquired through transfusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        The risk for temporary or permanent disability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        The ability to withdraw from participation at any time before surgery",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Registry",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The American Society of Transplant Surgeons",
"should implement a national registry for all living donor",
"liver transplant procedures",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     From: the American Society of Transplant Surgeons: Ethics Committee. Liver Transplant 2000; 6:815.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
