var title_f5_22_5486="Comparison of selected hormonal contraceptives";
var content_f5_22_5486=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F80393&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F80393&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristics, effectiveness, advantages, side effects and risks of selected methods of contraception",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"6\" width=\"16%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Method",
"       </td>",
"       <td class=\"subtitle1\">",
"        Characteristics",
"       </td>",
"       <td class=\"subtitle1\">",
"        Effectiveness",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Side effects and risks",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"6\">",
"        Combined estrogen-progestin contraception",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"7\">",
"        Oral contraceptive pill",
"       </td>",
"       <td>",
"        Mechanism of action: inhibition of ovulation; endometrial effects; cervical mucus effects; tubal peristalsis",
"       </td>",
"       <td rowspan=\"7\">",
"        <p>",
"         Perfect use: 99.7 percent",
"        </p>",
"        <p>",
"         Typical use: 92 percent",
"        </p>",
"       </td>",
"       <td>",
"        Effective and reversible",
"       </td>",
"       <td>",
"        Side effects: irregular bleeding or spotting; breast tenderness; nausea; headache",
"       </td>",
"       <td rowspan=\"7\">",
"        <p>",
"         Not associated with weight gain",
"        </p>",
"        <p>",
"         Pill-free intervals not necessary",
"        </p>",
"        <p>",
"         No limit to duration of use in healthy women",
"        </p>",
"        <p>",
"         Final height of adolescent users not affected",
"        </p>",
"        <p>",
"         Future fertility not affected",
"        </p>",
"        <p>",
"         May be used by healthy, nonsmoking women over age 35",
"        </p>",
"        <p>",
"         Not teratogenic",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1 pill daily: cyclically or continuously",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Noncontraceptive benefits:",
"       </td>",
"       <td class=\"sublist1_start\">",
"        Risks:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"5\">",
"        Initiation: first-day start (on first day of menses), Sunday start or \"quick start\" (at doctor's office)",
"       </td>",
"       <td class=\"sublist1\">",
"        - Cycle regulation; decreased menstrual flow; decreased dysmenorrhea",
"       </td>",
"       <td class=\"sublist1\">",
"        - Risk of venous thromboembolism: increased 3- to 4-fold; absolute risk is 1 to 1.5 events per 10,000 users per year of use; risk highest in first year of use",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Increased bone density",
"       </td>",
"       <td class=\"sublist1\">",
"        - No increased risk of myocardial infarction, cerebrovascular accident or gallbladder disease in healthy women",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Fewer perimenopausal symptoms",
"       </td>",
"       <td class=\"sublist1\" rowspan=\"3\">",
"        - Risk of breast cancer is increased only slightly if at all",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Less acne and hirsutism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        - Decreased risk of ovarian, endometrial and possibly colorectal cancer; fewer ovarian cysts; decreased incidence or severity of premenstrual symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"4\">",
"        Transdermal contraceptive patch",
"       </td>",
"       <td>",
"        Contains ethinyl estradiol and norelgestromin",
"       </td>",
"       <td rowspan=\"4\">",
"        <p>",
"         Perfect use: 99.7 percent",
"        </p>",
"        <p>",
"         Typical use: 92 percent",
"        </p>",
"       </td>",
"       <td>",
"        Effective and reversible",
"       </td>",
"       <td>",
"        Side effects: similar to those of oral contraceptives; local skin irritation in 20 percent",
"       </td>",
"       <td rowspan=\"4\">",
"        <p>",
"         May be less effective in women weighing &gt;90 kg",
"        </p>",
"        <p>",
"         Higher overall estrogen dose, but lower peak levels, than with oral contraceptive",
"        </p>",
"        <p>",
"         Effect of avoiding first-pass metabolism in liver uncertain",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mechanism of action: same as that of oral contraceptive",
"       </td>",
"       <td>",
"        Once-a-week dosing schedule",
"       </td>",
"       <td>",
"        Patch detachment (uncommon)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        1 patch weekly: cyclically (1 patch weekly for 3 weeks, than 1 patch-free week) or continuously",
"       </td>",
"       <td>",
"        48-hour \"window of forgiveness\"",
"       </td>",
"       <td rowspan=\"2\">",
"        Risks: similar to those of oral contraceptive; possibly increased risk of venous thromboembolism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Noncontraceptive benefits similar to those of oral contraceptive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"4\">",
"        Vaginal contraceptive ring",
"       </td>",
"       <td>",
"        Contains ethinyl estradiol and etonorgestrel",
"       </td>",
"       <td rowspan=\"4\">",
"        <p>",
"         Perfect use: 99.7 percent",
"        </p>",
"        <p>",
"         Typical use: 92 percent",
"        </p>",
"       </td>",
"       <td>",
"        Effective and reversible",
"       </td>",
"       <td>",
"        <p>",
"         Side effects: similar to those of oral contraceptive",
"        </p>",
"        <p>",
"         Ring-specific side effects: vaginitis (5.6 percent), leukorrhea (4.6 percent), vaginal discomfort (2.4 percent)",
"        </p>",
"       </td>",
"       <td rowspan=\"4\">",
"        <p>",
"         Vaginal spermicides and antifungals have no effect on ring efficacy",
"        </p>",
"        <p>",
"         Use does not worsen low-grade squamous intraepithelial lesions",
"        </p>",
"        <p>",
"         Effect of avoiding first-pass metabolism in liver uncertain",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mechanism of action: same as that of oral contraceptive",
"       </td>",
"       <td>",
"        Once-a-month dosing schedule",
"       </td>",
"       <td>",
"        Expulsion (uncommon)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        1 ring monthly: cyclically (1 ring for 21 days, then 7-day ring-free interval) or continuously",
"       </td>",
"       <td>",
"        1-week \"window of forgiveness\"",
"       </td>",
"       <td>",
"        Uterovaginal prolapse or vaginal stenosis are relative contraindications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Noncontraceptive benefits similar to those of oral contraceptive",
"       </td>",
"       <td>",
"        Risks: similar safety profile as that of oral contraceptive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"6\">",
"        Progestin-only contraception",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"3\">",
"        Progestin-only pill",
"       </td>",
"       <td>",
"        Contains norethindrone",
"       </td>",
"       <td rowspan=\"3\">",
"        <p>",
"         Perfect use: 99.7 percent",
"        </p>",
"        <p>",
"         Typical use: 92 percent",
"        </p>",
"       </td>",
"       <td>",
"        Effective and reversible",
"       </td>",
"       <td>",
"        Side effects: irregular bleeding; headache; bloating; acne; breast tenderness",
"       </td>",
"       <td rowspan=\"3\">",
"        Although often used by breast-feeding women, it may be used by any woman seeking reliable, reversible contraception",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mechanism of action: cervical mucus changes; impaired sperm motility; possible inhibition of ovulation",
"       </td>",
"       <td rowspan=\"2\">",
"        Can be taken by women with contraindications to estrogen",
"       </td>",
"       <td rowspan=\"2\">",
"        Risks: no apparent increased risk of venous thromboembolism or cerebrovascular accident",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1 pill daily: no pill-free interval; must be taken at same time every day (back-up method of contraception required if &gt;27 hours between pills)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Fisher WA, Black A. Review: Contraception in Canada: a review of method choices, characteristics, adherence and approaches to counseling. CMAJ 2007; 176:953. Copyright &copy; 2007 Canadian Medical Association.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
