var title_f5_25_5533="Oral medications for dystonia";
var content_f5_25_5533=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F75422&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F75422&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Oral medications for the treatment of dystonia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Pharmacologic agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Efficacy and comment",
"       </td>",
"       <td class=\"subtitle1\">",
"        Side effects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Dopamine agonists",
"       </td>",
"       <td rowspan=\"2\">",
"        Dramatic response in the dopa responsive form of dystonia; effective in 10 to 15 percent of patients with other types of dystonia; more rapid upward titration possible",
"       </td>",
"       <td rowspan=\"2\">",
"        Nausea (especially at initiation of therapy); may worsen dystonia; rapid discontinuation possible",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Carbidopa/Levodopa",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist1_start\">",
"        Anticholinergic/antihistaminic",
"       </td>",
"       <td rowspan=\"6\">",
"        Effective in approximately 40 percent of patients, mainly children; benefit limited by side effects; requires slow upward titration",
"       </td>",
"       <td rowspan=\"6\">",
"        Dry mouth (may lead to dental caries); blurred vision; exacerbation of acute-angle glaucoma; urinary retention; memory problems; sedation; confusion; hallucinations; heat intolerance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Trihexyphenidyl",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Benztropine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Procyclidine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Diphenhydramine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Ethopropazine",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Baclofen",
"       </td>",
"       <td>",
"        Effective in approximately 20 percent of patients; high doses tolerated in children; benefits limited by side effects; intrathecal baclofen minimally successful; withdrawal effects on sudden discontinuation",
"       </td>",
"       <td>",
"        Nausea; sedation; dysphoria; muscle weakness (in those with spasticity associated)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Clonazepam",
"       </td>",
"       <td>",
"        Effective in approximately 15 percent of patients; possibility for addiction; withdrawal effects on sudden discontinuation",
"       </td>",
"       <td>",
"        Sedation; depression; confusion; dependence",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist1_start\">",
"        Muscle relaxants",
"       </td>",
"       <td rowspan=\"3\">",
"        Limited benefit in some patients; side effects frequent",
"       </td>",
"       <td rowspan=\"3\">",
"        Sedation; dysphoria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Tizanidine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cyclobenzaprine",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist1_start\">",
"        Anticonvulsant medications",
"       </td>",
"       <td rowspan=\"3\">",
"        Benefit in less than 10 percent of patients",
"       </td>",
"       <td rowspan=\"3\">",
"        Ataxia; sedation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Carbamazepine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Gabapentin",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist1_start\">",
"        Dopamine-depleting agents",
"       </td>",
"       <td rowspan=\"3\">",
"        Requires a very slow upward titration (4 weeks between dose increases)",
"       </td>",
"       <td rowspan=\"3\">",
"        Depression; dysphoria; parkinsonism",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Tetrabenazine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Reserpine",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Dopamine receptor blocking agents",
"       </td>",
"       <td>",
"        Effective in up to 25 percent of patients; clozapine requires weekly blood counts and may cause life threatening agranulocytosis",
"       </td>",
"       <td>",
"        The possibility of tardive dyskinesia and the other adverse effects from this class of medications severely limits usefulness; not recommended for dystonia",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
