" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical manifestations of malaria",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?5/26/5543/contributors\">",
