var title_f5_27_5564="Clinical classification of ONJ related to bone modifying agents";
var content_f5_27_5564=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F69275&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F69275&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of osteonecrosis of the jaw (ONJ) in patients receiving therapy with a bone modifying agent",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        BRONJ stage",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"       <td class=\"subtitle1\">",
"        Treatment strategies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        At risk category",
"       </td>",
"       <td>",
"        No apparent necrotic bone in patients who have been treated with either oral or IV bisphosphonates",
"       </td>",
"       <td>",
"        No treatment",
"        <br/>",
"        Patient education",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage 0",
"       </td>",
"       <td>",
"        No clinical evidence of necrotic bone, but non specific clinical findings and symptoms",
"       </td>",
"       <td>",
"        Systemic therapies including pain medications and antibiotics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage I",
"       </td>",
"       <td>",
"        Non-symptomatic lesions with bone exposure in absence of&nbsp;signs of infection",
"       </td>",
"       <td>",
"        Topical antiseptic therapy",
"        <br/>",
"        Follow-up",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage II",
"       </td>",
"       <td>",
"        Bone exposure with pain, infection, and swelling in the area of the&nbsp;lesion",
"       </td>",
"       <td>",
"        Oral antibiotics, antibacterial mouth rinse, pain control",
"        <br/>",
"        Superficial debridement to relieve soft tissue irritation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Stage III",
"       </td>",
"       <td>",
"        Bone exposure, pain, inflammation, maxillary sinus involvement, cutaneous fistulas, and pathological fractures",
"       </td>",
"       <td>",
"        Antibacterial mouth rinse",
"        <br/>",
"        Antibiotic therapy and pain control",
"        <br/>",
"        Surgical debridement and resection for longer term palliation of infection and pain",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     BRONJ: bisphosphonate-induced osteonecrosis of the jaw; AAOMS: American Association of Oral and Maxillofacial Surgeons; IV: intravenous.",
"    </div>",
"    <div class=\"reference\">",
"     Reprinted by permission of Edizioni Minerva Medica from: Vescovi P, Nammour S. Bisphosphonate-related osteonecrosis of the jaw (BRONJ) therapy. A critical review. Minerva Stomatol 2010; 59:181. Copyright &copy; 2010.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
