" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 525px\">",
"   <div class=\"ttl\">",
"    Thyroid nodule",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 505px; height: 482px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
