var title_f5_33_5659="Helpful clues in recognizing HP";
var content_f5_33_5659=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F55505&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F55505&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Helpful clues in the recognition of hypersensitivity pneumonitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        History of recurrent atypical pneumonia, particularly if there is regularity to the illness (eg, only in spring or fall, only certain time of week or following certain events)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symptoms developed after moving to new job or home",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patient or any family member has pets (especially birds), even if the patient denies contact with the pets",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History of water damage to home or office, particularly to crawl space, basement, roof, ceiling tiles, carpet (even if previously cleaned)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use of hot tub, sauna, swimming pool",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Other people have similar symptoms or have left work because of recurrent symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patient feels better when away from home or work (such as after a vacation)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
