var title_f5_35_5692="Common symptoms IEI";
var content_f5_35_5692=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F57038&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F57038&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Common symptoms in patients with idiopathic environmental intolerance",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Organ system",
"       </td>",
"       <td class=\"subtitle1\">",
"        Symptom",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Head, eye, ear, nose, and throat",
"       </td>",
"       <td>",
"        Eye burning or pruritis, tinnitus, buzzing, sudden deafness, visual impairment, rhinorrhea, nasal obstruction, odor sensitivity, glossodynia, pharyngeal irritation, dysphonia, hoarseness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pulmonary",
"       </td>",
"       <td>",
"        Dyspnea, cough, chest pain, shortness of breath",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cardiovascular",
"       </td>",
"       <td>",
"        Palpitations, irregular heartbeat, tachycardia, chest pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gastrointestinal",
"       </td>",
"       <td>",
"        Stomach pain, gastritis, indigestion, dyspepsia, anorexia, constipation, cramps, nausea, vomiting, diarrhea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Genitourinary",
"       </td>",
"       <td>",
"        Dysmenorrhea, vaginitis, dysuria, urinary frequency, urinary retention",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Musculoskeletal",
"       </td>",
"       <td>",
"        Myalgia, weakness, muscle tension, arthralgia, skin irritation, pruritus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Endocrine",
"       </td>",
"       <td>",
"        Hot flashes, chills, polydipsia, hair loss",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lymphatic",
"       </td>",
"       <td>",
"        Change in size of lymph nodes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dermatologic",
"       </td>",
"       <td>",
"        Diaphoresis, pruritis, burning, rash, alopecia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Neurologic",
"       </td>",
"       <td>",
"        Headaches, paresthesias, paralysis, seizures, convulsions, dizziness, light headedness, weakness, pain syndromes, tremor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Psychiatric",
"       </td>",
"       <td>",
"        Fatigue, drowsiness, irritability, hyperactivity, insomnia, mental slowing or confusion, inattention, poor concentration, memory deficits, depression, anxiety, irritability, anhedonia, mood lability, crying spells",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
