"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Pharmacologic agent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Class",
"       </td>",
"       <td class=\"subtitle1\">",
