var title_f5_37_5724="Medication interactions travel and transplant";
var content_f5_37_5724=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F61057&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F61057&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Interactions between common travel and transplant medications",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Calcineurin inhibitors (CNI)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Trimethoprim/sulfamethoxazole",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acetazolamide (Diamox)",
"       </td>",
"       <td>",
"        May increase levels",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Artemether/lumefantrine (Coartem)",
"       </td>",
"       <td>",
"        May enhance the QT-prolonging effect",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Atovaquone/proguanil (Malarone)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        May increase risk of bone marrow toxicity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Azithromycin (Zithromax)",
"       </td>",
"       <td>",
"        May increase levels",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chloroquine (Aralen)",
"       </td>",
"       <td>",
"        May increase levels; may enhance the QT-prolonging effect",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Doxycycline",
"       </td>",
"       <td>",
"        May increase levels",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mefloquine (Lariam)",
"       </td>",
"       <td>",
"        May increase levels; may enhance the QT-prolonging effect",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Primaquine",
"       </td>",
"       <td>",
"        May increase levels",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sulfadoxine/pyrimethamine (Fansidar)",
"       </td>",
"       <td>",
"        May decrease levels",
"       </td>",
"       <td>",
"        May increase risk of bone marrow toxicity",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Significant interactions of azathioprine, mycophenolate mofetil, and corticosteroids with travel medicines and have not been reported.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: MicroMedex DrugReax Interactive Drug Interactions and Lexicomp Online Interaction Analysis.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
