var title_f5_39_5755="Treatment for mild pediatric lead poisoning - level 15 to 19";
var content_f5_39_5755=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F53709&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F53709&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment strategies for patients with venous lead levels 15 mcg/dL (0.72 micromol/L)  to 19 mcg/dL (0.91 micromol/L)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        1.",
"       </td>",
"       <td>",
"        Report to appropriate health authorities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2.",
"       </td>",
"       <td>",
"        Perform history, physical examination, and laboratory examinations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3.",
"       </td>",
"       <td>",
"        Repeat lead level in one month, then every three to four months until &lt;5 mcg/dL (0.24 micromol/L). If the lead level is not &lt;15 mcg/dL (0.72 micromol/L) with the second or third testing, refer for environmental abatement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4.",
"       </td>",
"       <td>",
"        Provide parents with educational and nutritional counseling",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
