var title_f5_39_5757="NHLBI panel screening guidelines for pediatric dyslipidemia";
var content_f5_39_5757=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F76041&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F76041&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pediatric dyslipidemia screening guidelines from the 2011 Expert Panel Integrated Guidelines for Cardiovascular Health and Risk Reduction in Children and Adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Birth-2 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No lipid screening",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        2-8 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Selective screening using fasting lipid profile (FLP) two times*, based on a positive history of one of the following:",
"        <ul>",
"         <li>",
"          Parent, grandparent, aunt/uncle, or sibling with coronary heart disease (ie, myocardial infarction, angina, stroke, coronary artery bypass graft/stent/angioplasty) at &lt;55 years in males, &lt;65 years in females",
"         </li>",
"         <li>",
"          Parent with TC &ge;240 mg/dL or known dyslipidemia",
"         </li>",
"         <li>",
"          Patient&nbsp;has diabetes, hypertension, BMI &ge;95th percentile, or smokes cigarettes or is exposed to second hand smoke",
"         </li>",
"         <li>",
"          Patient has a moderate- or high-risk medical condition associated with cardiovascular disease (ie, chronic kidney disease, recipient of a cardiac transplant, Kawasaki disease with current or regressed coronary artery disease, or chronic inflammatory disease)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        9-11 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Universal screening with a nonfasting lipid screening using non-HDL C levels.",
"        </p>",
"        <p>",
"         Further evaluation based is recommended with measurement of&nbsp;FLP two times* with&nbsp;a positive result of either a nonfasting non-HDL-C level &ge;145 mg/dL, and HDL &lt;40 mg/dL, or a FLP with LDL-C &ge;130 mg/dL, non-HDL-C &ge;145mg/dL, HDL-C &lt;40 mg/dL or TG &ge;130 mg/dL.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        12-16 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Universal screening",
"        <strong>",
"         not",
"        </strong>",
"        recommended because of normal changes in lipid levels during puberty, which decrease the sensitivity and specificity for predicting adult LDL-C levels and increase false-negative results in this age group.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Selective screening using fasting lipid profile (FLP) two times*, if new knowledge of one of the following:",
"        <ul>",
"         <li>",
"          Family history of coronary heart disease (ie, parent, grandparent, or aunt/uncle)",
"         </li>",
"         <li>",
"          Parent with TC &ge;240 mg/dL or known dyslipidemia",
"         </li>",
"         <li>",
"          Patient has diabetes, hypertension, BMI &ge;85th percentile or smokes cigarettes",
"         </li>",
"         <li>",
"          Patient has a moderate- or high-risk medical condition associated with cardiovascular disease (ie, chronic kidney disease, recipient of a cardiac transplant, Kawasaki disease with current or regressed coronary artery disease, or chronic inflammatory disease)",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        17-21 years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Universal screening once during this time period with a nonfasting lipid screening using non-HDL C levels.",
"        </p>",
"        <p>",
"         Further evaluation based is recommended with measurement of FLP two times* with a positive result of either a nonfasting non-HDL-C level &ge;145 mg/dL, and HDL &lt;40 mg/dL, or a FLP with LDL-C &ge;130 mg/dL, non-HDL-C &ge;145mg/dL, HDL-C &lt;40 mg/dL or TG &ge;130 mg/dL.",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     NHLBI: National heart lung, blood institute; BMI: body mass index; TC: total cholesterol; LDL-C: low-density lipoprotein cholesterol; HDL-C: high-density lipoprotein cholesterol; TG: triglycerides.",
"     <br>",
"      * Interval between FLP measurements: after 2 weeks but within 3 months.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Daniels SR, Benuck I, Christakis DA, et al. Expert panel on integrated guidelines for cardiovascular health and risk reduction in children and adolescents: Full report, 2011. National Heart Lung and Blood Institute. Available at:",
"     <a href=\"file://www.nhlbi.nih.gov/guidelines/cvd_ped/peds_guidelines_full.pdf\" target=\"_blank\">",
"      file://www.nhlbi.nih.gov/guidelines/cvd_ped/peds_guidelines_full.pdf",
"     </a>",
"     .",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
