" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Congenital erythropoietic porphyria",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
