" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Long-term management of patients with anaphylaxis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?5/44/5833/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?5/44/5833/contributors\">",
"     F Estelle R Simons, MD, FRCPC",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?5/44/5833/contributors\">",
