" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Post-polio syndrome",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?5/45/5849/contributors\">",
