var title_f5_45_5851="Rx chancroid";
var content_f5_45_5851=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F66035&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F66035&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment options for chancroid*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Drugs of choice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Azithromycin 1 g oral once",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ceftriaxone 250 mg IM once",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Alternatives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ciprofloxacin&bull; 500 mg oral twice daily for three days",
"        <strong>",
"         OR",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Erythromycin&Delta; 500 mg oral four times daily for seven days",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * All regimens, especially single-dose ceftriaxone, are less effective in HIV-infected patients.",
"     <br>",
"      &bull; Not recommended in pregnancy.",
"      <br>",
"       &Delta; Erythromycin ethylsuccinate 800 mg may be substituted for erythromycin base 500 mg; erythromycin estolate is contraindicated in pregnancy.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Drugs for sexually transmitted diseases. Treat Guidel Med Lett 2004; 2:67.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
