var title_f5_48_5900="Contraindications TTO2";
var content_f5_48_5900=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F73278&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F73278&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Contraindications to transtracheal oxygen",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Relative contraindications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        PaO",
"        <sub>",
"         2",
"        </sub>",
"        &lt;55 mgHg at oxygen flow rate of 8 L/min or greater",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        PaCO",
"        <sub>",
"         2",
"        </sub>",
"        &gt;50 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        FEV1 &lt;500 mL or less than 70 percent of baseline",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anatomic abnormality that makes insertion difficult (severe obesity, deviated trachea, previous tracheostomy)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Coagulopathy, or medications that increase bleeding risk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Risk of delayed healing (diabetes mellitus, glucocorticoid dependence, connective tissue disease)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Uncontrolled bronchial hyperreactivity (eg, greater than 12 percent increase in post-bronchodilator spirometry)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Poor mechanical reserve, weak cough",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Copious or viscous sputum production",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mild to moderate anxiety",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Absolute contraindications",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inability to practice self-care, including periodic stripping of catheter",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Uncompensated (acute) respiratory acidosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe coagulopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pleura herniated over puncture site",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Conditions that predispose to upper airway obstruction (subglottic stenosis, bilateral vocal cord paralysis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe anxiety",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Poor adherence to medical regimen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Medically unstable",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
