var title_f5_52_5964="Est dx MCTD";
var content_f5_52_5964=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F74610&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F74610&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Two algorithms for establishing a diagnosis of mixed connective tissue disease (MCTD)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Alarcon-Segovia's criteria",
"       </td>",
"       <td class=\"subtitle1\">",
"        Kahn's criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        A. Serological criteria",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        A. Serological criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anti-RNP antibodies with a hemagglutination titer of &ge;1:1600",
"       </td>",
"       <td class=\"indent1\">",
"        High titer anti-RNP corresponding to a speckled ANA of &ge;1:1200 titer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        B. Clinical criteria",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        B. Clinical criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1. Swollen hands",
"       </td>",
"       <td class=\"indent1\">",
"        1. Swollen fingers",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2. Synovitis",
"       </td>",
"       <td class=\"indent1\">",
"        2. Synovitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3. Myositis*",
"       </td>",
"       <td class=\"indent1\">",
"        3. Myositis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        4. Raynaud's phenomenon",
"       </td>",
"       <td class=\"indent1\">",
"        4. Raynaud phenomenon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        5. Acrosclerosis",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        MCTD is present if:",
"       </td>",
"       <td class=\"subtitle2_left\">",
"        MCTD is present if:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Criterion A is accompanied by 3 or more clinical criteria - one of which must include synovitis or myositis.",
"       </td>",
"       <td>",
"        Criterion A is accompanied by Raynaud phenomenon and 2 or more of the 3 remaining clinical criteria.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Myalgia is commonly substituted for myositis.",
"    </div>",
"    <div class=\"reference\">",
"     Original figure modified for this publication. Bennett RM. Overlap Syndromes. Kelley's Textbook of Rheumatology, 8th Edition, W.B. Saunders Co, Philadelphia 2009. Illustration used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
