var title_f5_53_5979="Periop factors aggravating OSA";
var content_f5_53_5979=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F71284&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F71284&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Factors contributing to acute perioperative aggravation of obstructive sleep apnea (OSA)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Depressant influences of anesthetics/sedatives/analgesics on central respiratory drive, upper airway protective reflexes, and arousal responses",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Intraluminal upper airway narrowing due to post-endotracheal intubation edema, nasal packings, nasal tubes, or hematomas",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Forced supine positioning",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Sleep deprivation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Intense rapid eye movement (REM) rebound",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Temporary suspension of continuous positive airway pressure (CPAP) therapy due to pain, anxiety, nausea, restlessness, presence of a nasogastric tube, failure to bring the equipment to the hospital, or lack of planning for post-operative application.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
