var title_f5_53_5980="Clinical features of psychogenic nonepileptic seizures";
var content_f5_53_5980=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F77773&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F77773&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    More common features distinguishing epileptic seizures and psychogenic nonepileptic seizures*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Sign",
"       </td>",
"       <td class=\"subtitle1\">",
"        Epileptic",
"       </td>",
"       <td class=\"subtitle1\">",
"        PNES",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Duration",
"       </td>",
"       <td>",
"        Usually brief, less than 1-2 minutes",
"       </td>",
"       <td>",
"        Usually longer than 2 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eyes",
"       </td>",
"       <td>",
"        Eyes usually open during event",
"       </td>",
"       <td>",
"        <p>",
"         Eyes often closed",
"        </p>",
"        <p>",
"         Forced eye closure suggests PNES",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Motor activity",
"       </td>",
"       <td>",
"        <p>",
"         Stereotyped",
"        </p>",
"        <p>",
"         Synchronized",
"        </p>",
"        <p>",
"         Build, progress",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Variable",
"        </p>",
"        <p>",
"         Forward pelvic thrusting, rolling side to side, opisthotonus",
"        </p>",
"        <p>",
"         Wax and wane",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vocalization",
"       </td>",
"       <td>",
"        Uncommon, especially during convulsion",
"       </td>",
"       <td>",
"        May occur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Prolonged ictal atonia",
"       </td>",
"       <td>",
"        Very rare",
"       </td>",
"       <td>",
"        May occur",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Incontinence",
"       </td>",
"       <td>",
"        Common in convulsive seizures",
"       </td>",
"       <td>",
"        Less common",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Autonomic signs",
"       </td>",
"       <td>",
"        Cyanosis, tachycardia common with major convulsion",
"       </td>",
"       <td>",
"        Uncommon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Postictal symptoms",
"       </td>",
"       <td>",
"        <p>",
"         Usually confused, drowsy",
"        </p>",
"        <p>",
"         Headache common",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         May rapidly awaken and reorient",
"        </p>",
"        <p>",
"         Headache rare",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * No single feature is sensitive or specific for epileptic versus psychogenic nonepileptic seizures.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
