" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for certification for commercial drivers with bundle branch blocks",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Diagnosis",
"      </td>",
"      <td class=\"subtitle1\">",
"       Physiologic/functional",
