var script_f5_55_6013=[""].join("\n");
var outline_f5_55_6013=null;
var title_f5_55_6014="Management IRIS A";
var content_f5_55_6014=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F65960%7EID%2F78018&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F65960%7EID%2F78018&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    General management recommendations for specific IRIS-related syndromes*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Pathogen",
"       </td>",
"       <td class=\"subtitle1\">",
"        Most common clinical manifestations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Management recommendations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Prognosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        General IRIS",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Consider concurrent unmasked infection, persistent active infection, antimicrobial resistance, adverse drug reaction, and medicine nonadherence.",
"       </td>",
"       <td>",
"        Generally self-limited",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        M. tuberculosis",
"       </td>",
"       <td>",
"        <p>",
"         Pneumonia",
"        </p>",
"        <p>",
"         Lymphadenitis",
"        </p>",
"        <p>",
"         Intracranial tuberculomas",
"        </p>",
"        <p>",
"         Meningitis",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Consider the possibility of multidrug resistant TB.",
"        </p>",
"        <p>",
"         Incise and drain the initial episode of adenitis for diagnostic purposes and symptomatic relief.",
"        </p>",
"        <p>",
"         For moderate severity or CNS involvement, prednisone (1.0 mg/kg, up to 80mg daily) or dexamethasone 8-16 mg/day divided in twice daily doses and tapered after 1-2 months. Use as adjunct to anti-tuberculous therapy.",
"        </p>",
"        <p>",
"         Continue HAART&bull;.",
"        </p>",
"       </td>",
"       <td>",
"        Generally self-limited",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        M. avium complex",
"       </td>",
"       <td>",
"        <p>",
"         Lymphadenitis",
"        </p>",
"        <p>",
"         Pneumonia",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Incision and drainage or aspiration for the initial episode of adenitis.",
"        </p>",
"        <p>",
"         If illness is severe, adjunct corticosteroids (as above) in addition to standard anti-MAC therapy.",
"        </p>",
"        <p>",
"         Continue HAART.",
"        </p>",
"       </td>",
"       <td>",
"        Self-limited in the vast majority",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cryptococcus spp.",
"       </td>",
"       <td>",
"        <p>",
"         Meningoencephalitis",
"        </p>",
"        <p>",
"         Pneumonia",
"        </p>",
"        <p>",
"         Lymphadenitis",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Continue antifungal therapy.",
"        </p>",
"        <p>",
"         Adjunct corticosteroids, if illness is severe or not self-limited.",
"        </p>",
"        <p>",
"         Continue HAART&bull;.",
"        </p>",
"       </td>",
"       <td>",
"        Generally self-limited",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pneumocystis carinii",
"       </td>",
"       <td>",
"        Pneumonia",
"       </td>",
"       <td>",
"        <p>",
"         Standard course of anti-Pneumocystis antimicrobial therapy.",
"        </p>",
"        <p>",
"         Adjunct corticosteroids.",
"        </p>",
"        <p>",
"         Continue HAART&bull;.",
"        </p>",
"       </td>",
"       <td>",
"        Generally self-limited when corticosteroid therapy is used.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cytomegalovirus",
"       </td>",
"       <td>",
"        Uveitis/vitritis",
"       </td>",
"       <td>",
"        <p>",
"         Continue HAART&Delta;.",
"        </p>",
"        <p>",
"         Anti-CMV antiviral therapy if concurrent CMV retinitis is present.",
"        </p>",
"        <p>",
"         Topical, intraocular, or systemic corticosteroids in conjunction with close ophthalmologic follow-up&loz;.",
"        </p>",
"        <p>",
"         Vitrectomy may be needed if vitreomacular traction occurs.",
"        </p>",
"       </td>",
"       <td>",
"        Generally good, but long-term sight-threatening complications may occur.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Based on published clinical experience, but needs to be proven in controlled clinical trials.",
"     <br>",
"      &bull; Consider delaying HAART therapy for 1-2 months if not already begun.",
"      <br>",
"       &Delta; Consider temporarily interrupting HAART for 1-3 months if eye, neural, liver, or other organ involvement is particularly severe, progressive, and/or steroid refractory.",
"       <br>",
"        &loz; Can alternatively use topical or systemic non-steroidal anti-inflammatory agents.",
"       </br>",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    General management recommendations for IRIS-related syndromes, continued*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Pathogen",
"       </td>",
"       <td class=\"subtitle1\">",
"        Most common clinical manifestations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Management recommendations",
"       </td>",
"       <td class=\"subtitle1\">",
"        Prognosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        JC virus",
"       </td>",
"       <td>",
"        Progressive multifocal leukoencephalopathy with inflammatory features",
"       </td>",
"       <td>",
"        <p>",
"         Continue HAART in most cases.",
"        </p>",
"        <p>",
"         Corticosteroids.",
"        </p>",
"       </td>",
"       <td>",
"        Often self-limited, but chronic neurologic sequelae and fatal cases may occur.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        HSV, VZV",
"       </td>",
"       <td>",
"        <p>",
"         Perianal herpes",
"        </p>",
"        <p>",
"         Localized zoster",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Continue HAART.",
"        </p>",
"        <p>",
"         Oral acyclovir or famciclovir for 7-14 days.",
"        </p>",
"       </td>",
"       <td>",
"        Self-limited in the vast majority",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hepatitis B",
"       </td>",
"       <td>",
"        Hepatitis",
"       </td>",
"       <td>",
"        <p>",
"         Consider direct drug hepatotoxicity (eg, protease inhibitor).",
"        </p>",
"        <p>",
"         Continue or interrupt HAART&Delta;.",
"        </p>",
"        <p>",
"         Tenofovir, lamivudine, emtricitabine, and/or entecavir if indicated.",
"        </p>",
"       </td>",
"       <td>",
"        Often self-limited, but progressive cirrhosis may occur, esp with low functional hepatic reserve.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hepatitis C",
"       </td>",
"       <td>",
"        Hepatitis",
"       </td>",
"       <td>",
"        <p>",
"         Consider direct drug hepatotoxicity (eg, protease inhibitor).",
"        </p>",
"        <p>",
"         Continue or interrupt HAART&Delta;.",
"        </p>",
"        <p>",
"         Antiviral therapy may be an option in selected patients.",
"        </p>",
"       </td>",
"       <td>",
"        Same as above.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Parvovirus B19",
"       </td>",
"       <td>",
"        <p>",
"         Fever, anemia",
"        </p>",
"        <p>",
"         Encephalitis",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Intravenous immunoglobulin (IVIG).",
"        </p>",
"        <p>",
"         Continue or interrupt HAART&Delta;.",
"        </p>",
"       </td>",
"       <td>",
"        Usually self-limited.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sarcoidosis",
"       </td>",
"       <td>",
"        Pulmonary",
"       </td>",
"       <td>",
"        <p>",
"         Corticosteroids.",
"        </p>",
"        <p>",
"         Continue HAART.",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Autoimmune thyroiditis",
"       </td>",
"       <td>",
"        Thryotoxicosis",
"       </td>",
"       <td>",
"        <p>",
"         Anti-thyroid drugs, beta-blockers, and/or thyroid ablation.",
"        </p>",
"        <p>",
"         Continue HAART.",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Based on published clinical experience, but needs to be proven in controlled clinical trials.",
"     <br>",
"      &Delta; Consider temporarilyinterrupting HAART for 1-3 months if eye, neural, liver, or other organinvolvement is particularly severe, progressive, and/or steroidrefractory.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
