var title_f5_60_6093="Dietary guidelines children";
var content_f5_60_6093=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F79084&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F79084&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Dietary guidelines for children older than one year",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        General guidelines",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A variety of nutrient-dense foods from the basic food groups (milk/milk products, meat/protein, grains, fruits/vegetables) should be offered each day.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Foods and beverages should contain or be prepared with little added salt, sugar, or caloric sweeteners.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Fat",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1 to 2 years: Fat and cholesterol intake are not restricted.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 to 3 years: Fat should comprise 30 to 35 percent of total energy intake; saturated fats should be limited to &lt;10 percent of total energy intake, cholesterol should be limited to &lt;300 mg/day; intake of trans fats should be as low as possible.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4 to 18 years: Fat should comprise 25 to 35 percent of total energy intake; fat intake should not be restricted to &lt;20 percent of total energy intake; saturated fats should be limited to &lt;10 percent of total energy intake, cholesterol should be limited to &lt;300 mg/day; intake of trans fats should be as low as possible.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Meat/protein",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Select and prepare meat, poultry, fish, and dried beans with as little fat as possible.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Fruits and vegetables",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A colorful variety of fruits and vegetables should be offered each day.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Whole fruit is preferred to fruit juice, but one-half of the recommended daily servings can be provided in the form of 100 percent fruit juice.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consumption of 100 percent fruit juice should be limited to 4 to 6 ounces in children aged 1 to 6 years and 8 to 12 ounces in children older than 7 years.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Grains",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        At least one-half of total grains consumed should be whole grains. When reading the label, \"whole grain\" should be the first ingredient.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Cow's milk",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1 to 2 years: At least 2 cups of whole milk per day (or equivalent products).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 to 8 years: At least 2 to 3 cups of fat-free or low-fat milk per day (or equivalent products).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &ge;9 years: At least 3 cups of fat-free or low-fat milk per day (or equivalent products).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Beverages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plain, unflavored water is the preferred beverage for children, particularly when fluids are consumed outside of meals and snacks.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br>",
"      <ol>",
"       <li>",
"        Committee on Nutrition American Academy of Pediatrics. Feeding the Child. In: Pediatric Nutrition Handbook, 6th ed, Kleinman RE (Ed), American Academy of Pediatrics, Elk Grove Village, IL 2009. p.145.",
"       </li>",
"       <li>",
"        US Department of Health and Human Services. Dietary Guidelines for Americans, 2010.",
"        <a href=\"file://www.health.gov/dietaryguidelines/\" target=\"_blank\">",
"         file://www.health.gov/dietaryguidelines/",
"        </a>",
"        (Accessed on June 03, 2011).",
"       </li>",
"       <li>",
"        Gidding SS, Dennison BA, Birch LL, et al. Dietary recommendations for children and adolescents: a guide for practitioners: consensus statement from the American Heart Association. Circulation 2005; 112:2061.",
"       </li>",
"       <li>",
"        Institute of Medicine. Dietary reference intakes for calcium and vitamin D. Available at:",
"        <a href=\"file://www.iom.edu/Reports/2010/Dietary-Reference-Intakes-for-Calcium-and-Vitamin-D.aspx\" target=\"_blank\">",
"         www.iom.edu/Reports/2010/Dietary-Reference-Intakes-for-Calcium-and-Vitamin-D.aspx",
"        </a>",
"        . Accessed on October 4, 2011.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
