"",
"function CDAI_fx() {",
"with(document.CDAI_form){",
"",
"Score = 0.0;",
"doCalc = true;",
"",
"thisvalue = parseFloat(cc1.value);",
"if (isNaN(thisvalue)) thisvalue = 0;",
"ThisScore =  thisvalue * 14 + 0;",
"Score =  Score + ThisScore;",
"if (cc2.checked){",
"Score = Score + 30;",
"}",
"if (cc3[0].checked){",
"Score = Score + 0;",
"}",
"if (cc3[1].checked){",
"Score = Score + 35;",
"}",
"if (cc3[2].checked){",
"Score = Score + 70;",
"}",
"if (cc3[3].checked){",
"Score = Score + 105;",
"}",
"if (cc4[0].checked){",
"Score = Score + 0;",
"}",
"if (cc4[1].checked){",
"Score = Score + 49;",
"}",
"if (cc4[2].checked){",
"Score = Score + 98;",
"}",
"if (cc4[3].checked){",
"Score = Score + 147;",
"}",
"if (cc4[4].checked){",
"Score = Score + 196;",
"}",
"if (cc5.checked){",
"Score = Score + 20;",
"}",
"if (cc6.checked){",
"Score = Score + 20;",
"}",
"if (cc7.checked){",
"Score = Score + 20;",
"}",
"if (cc8.checked){",
"Score = Score + 20;",
"}",
"if (cc9.checked){",
"Score = Score + 20;",
"}",
"if (cc10.checked){",
