"}",
"thisvalue = parseFloat(cc12.value);",
"if (isNaN(thisvalue)) thisvalue = 0;",
"ThisScore =  thisvalue * 6 + 0;",
"Score =  Score + ThisScore;",
"thisvalue = parseFloat(cc13.value);",
"if (isNaN(thisvalue)) thisvalue = 0;",
"ThisScore =  thisvalue * 1 + 0;",
"Score =  Score + ThisScore;",
"cctotal.value = Score;",
"",
"if (doCalc){",
"",
"rrclr();",
