var content_f5_61_6108=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F59069&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F59069&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Key components of the diabetic foot exam",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Inspection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Dermatologic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Skin status: color, thickness, dryness, cracking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sweating",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Infection: check between toes for fungal infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ulceration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Calluses/blistering: hemorrhage into callus?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Musculoskeletal",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Deformity, eg, claw toes, prominent metatarsal heads, Charcot joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Muscle wasting (guttering between metatarsals)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Neurological assessment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        10-g monofilament +&nbsp;one of the following four",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vibration using 128-Hz tuning fork",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pinprick sensation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ankle reflexes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        VPT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Vascular assessment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Foot pulses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        ABI, if indicated",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reprinted with permission from: Boulton, AJM, Armstrong, DG, Albert, ST, et al. Comprehensive Foot Examination and Risk Assessment: A report of the Task Force of the Foot Care Interest Group of the American Diabetes Association, with endorsement by the American Association of Clinical Endocrinologists. Diabetes Care 2008; 31:1679. Copyright &copy;2008 American Diabetes Association.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
