var title_f5_63_6140="Possible causes for the increase in allergic disease";
var content_f5_63_6140=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ALLRG%2F79803&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ALLRG%2F79803&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Changes that may have contributed to the rise in allergic disease",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       I. Increased exposure to perennial indoor allergens",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Increased time spent indoors (1955-1995)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Improved conditions for mite and mold growth (1955-1975)*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Decreased ventilation*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Increased reservoirs for allergen (carpets/sofas) (1935-1965)*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       II. Changes in hygiene",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Clean water",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Separation from sewage (1900)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Chlorination (1920)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Eradication of helminths and malaria in Europe and USA (1920-1940)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Decline in Hepatitis A infection (1920-1990)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Introduction of broad spectrum antibiotics (&sim;1960)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Increased treatment and control of food to prevent bacterial infection (1900-1980)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Immunization to control infectious diseases (1800-1990)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       III. Lifestyle changes: Secondary to the introduction of sedentary entertainment in homes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Increase in the percentage of homes with television and increase in hours spent watching television (1955-1990)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Decline in outdoor \"play\" (1955-1995)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Rise in obesity (1970-2000)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Changes in diet following changes in traditional family life (1965-2000)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Decreased vitamin D levels secondary to decreased time spent outdoors and decreased intake",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    *Variable by locale",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
