var title_f5_63_6141="Interventions intracell calcium";
var content_f5_63_6141=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F75235&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F75235&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Interventions that theoretically alter diastolic cytosolic calcium levels",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Intervention",
"       </td>",
"       <td class=\"subtitle1\">",
"        Theoretical effect on diastolic Ca(i)2+",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanisms/comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ischemia or hypoxia",
"       </td>",
"       <td>",
"        Increase",
"       </td>",
"       <td>",
"        Decreased SR uptake and SL extrusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Digitalis",
"       </td>",
"       <td>",
"        Increase",
"       </td>",
"       <td>",
"        Increases NA(i)+ by Na+/K+ pump inhibition; Ca(i)2+ increased secondary to increased Na+ -Ca2+ exchange. Combination of ischemia or hypoxia + digitalis cause exaggerated diastolic dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ca2+ channel blocker",
"       </td>",
"       <td>",
"        Decrease",
"       </td>",
"       <td>",
"        Decreased Ca2+ entry by way of sarcolemma. Associated bradycardia prolongs diastole, allowing for greater calcium removal via SR and SL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &beta;-adrenergic blocker",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        Decreased c-AMP levels decrease Ca2+ entry via Ca2+-channel, but also decrease Ca2+ reuptake by SR. Associated bradycardia prolongs diastole as with Ca2+ channel blocker",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &beta;-adrenergic agonist",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        Increased c-AMP levels increase Ca2+ entry, but also increase SR calcium reuptake by phospholamban regulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phosphodiesterase inhibitors",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        Same mechanisms as &beta;-adrenergic agonist",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ca2+ agonist (BAYK8644)",
"       </td>",
"       <td>",
"        Increase",
"       </td>",
"       <td>",
"        Increased Ca2+ influx via L-type channels can produce Ca2+ overload",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ca2+ ionophore (A23187)",
"       </td>",
"       <td>",
"        Increase",
"       </td>",
"       <td>",
"        Increased transsarcolemmal Ca2+ influx",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Na+ agonist (vertridine)",
"       </td>",
"       <td>",
"        Increase",
"       </td>",
"       <td>",
"        [Ca2+]i loading by Na+/Ca2+ exchange",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Na+ ionophore (monensin)",
"       </td>",
"       <td>",
"        Increase",
"       </td>",
"       <td>",
"        [Ca2+]i loading by Na+/Ca2+ exchange",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Antagonist of Na+/Ca2+ exchanger (dichlorobenzamil)",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        Potential to affect [Na+]i",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &nbsp;Antagonist of Na+/H+ exchanger (amiloride)",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        Potential to affect [Na+]i",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibitor of SERCA2(caffeine)",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        Multiple effects but blocks Ca2+ uptake after inducing&nbsp;Ca2+ release",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        SR Ca2+ ATPase inhibitor (thapsigarin)",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        Blocks Ca2+ uptake by inhibiting pump",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inhibitor of RYR2 (ryanodine)",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        Blocks Ca2+ release channel of SR leading to Ca2+ overload of vesicles and decreased uptake",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phospholamban antibodies",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        May enhance SR Ca2+ uptake by modifying function of phospholamban",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mitochondrial inhibitors (FCCP)",
"       </td>",
"       <td>",
"        &plusmn;",
"       </td>",
"       <td>",
"        Decreased Ca2+ storage capacity of mitochondria in overload state",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    SERCA2: sarco(endo)plasmic reticulum Ca2+ ATPase pump; RYR2: ryanodine receptor 2.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
