var title_f5_7_5243="Criteria for adequate specimens";
var content_f5_7_5243=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F56119&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F56119&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cytologic criteria for an \"adequate\" specimen during TBNA",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Bronchial epithelial cells should be absent or rare.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A preponderance of lymphocytes should be found.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Strict criteria for cytologic diagnosis of malignancy should be established and adhered to, and all samples should be classified as negative, suspicious, or definitely malignant.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Only samples showing no evidence of pathology in the presence of lymphocytes should be considered true negatives.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from Minai, OA, Dasgupta, A, Mehta, AC. Transbronchial needle aspiration of central and peripheral lesions. In: Bolliger, CT, Mathur, PN (Eds), Progress in Respiratory Research-Interventional Bronchoscopy. Karger, Basel, Switzerland, 1999.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
