var title_f6_10_6315="Patch test relevance";
var content_f6_10_6315=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F71193&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F71193&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical relevance of patch test reactions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Definite",
"       </td>",
"       <td>",
"        Allergen found in patient's environment, dermatitis corresponds to point of contact with allergen, and dermatitis improved upon isolation of the allergen or recurred with rechallenge (provocative use test).",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Probable",
"       </td>",
"       <td>",
"        Same criteria as above but no ability to obtain follow-up information on improvement status or rechallenge.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Possible",
"       </td>",
"       <td>",
"        One criteria needs to be met from \"definite\".",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Not traced",
"       </td>",
"       <td>",
"        No evidence of current or past exposure.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Past",
"       </td>",
"       <td>",
"        Allergen found in past environment.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
