var title_f6_12_6348="Visual behavior infants";
var content_f6_12_6348=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F73947&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F73947&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Normal visual behavior of infants",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Age, months",
"       </td>",
"       <td class=\"subtitle1\">",
"        Behavior",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        0 to 1",
"       </td>",
"       <td>",
"        Turns eyes and head to look at light sources",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Horizontal tracking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Eye contact at 6 to 8 weeks",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        2 to 3",
"       </td>",
"       <td>",
"        Intense eye contact",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vertical and circular tracking",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interested in mobiles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interested in \"lip reading\"",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"5\">",
"        3 to 6",
"       </td>",
"       <td>",
"        Watches own hands",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reaches toward, later grasps, hanging objects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Observes toys falling and rolling away",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Shifts fixation across midline",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Visual sphere of attention widens gradually",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        7 to 10",
"       </td>",
"       <td>",
"        Notices small bread crumbs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        First touches them, then develops pincer grasp",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Interested in pictures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recognizes partially hidden objects",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        11 to 12",
"       </td>",
"       <td>",
"        Visual orientation at home",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Looks through window and recognizes people, recognizes pictures, plays hide-and-seek",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Hyvarinen L. Assessment of visually impaired infants. Ophthalmol Clin North Am 1994; 7:219. Copyright &copy; 1994 WB Saunders.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
