var title_f6_13_6364="High risk conditions for invasive pneumococcal disease";
var content_f6_13_6364=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F68655&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F68655&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Underlying medical conditions that are indications for pneumococcal polysaccharide vaccination among children, by risk group - Advisory Committee on Immunization Practices (ACIP), United States, 2010",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Risk group",
"       </td>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"5\">",
"        Immunocompetent children",
"       </td>",
"       <td>",
"        Chronic heart disease*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic lung disease",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diabetes mellitus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cerebrospinal fluid leaks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cochlear implant",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Children with functional or anatomic asplenia",
"       </td>",
"       <td>",
"        Sickle cell disease and other hemoglobinopathies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Congenital or acquired asplenia, or splenic dysfunction",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"4\">",
"        Children with immunocompromising conditions",
"       </td>",
"       <td>",
"        HIV infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic renal failure and nephrotic syndrome",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diseases associated with treatment with immunosuppressive drugs or radiation therapy, including malignant neoplasms, leukemias, lymphomas, and Hodgkin disease; or solid organ transplantation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Congenital immunodeficiency",
"        <sup>",
"         &Delta;",
"        </sup>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Particularly cyanotic congenital heart disease and cardiac failure.",
"     <br>",
"      <span class=\"bullet\">",
"       &bull;",
"      </span>",
"      Including asthma if treated with prolonged high-dose oral corticosteroids.",
"      <br>",
"       &Delta; Includes B- (humoral) or T-lymphocyte deficiency; complement deficiencies, particularly C1, C2, C3, and C4 deficiency; and phagocytic disorders (excluding chronic granulomatous disease).",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     From Centers for Disease Control and Prevention. Licensure of a 13-Valent Pneumococcal Conjugate Vaccine (PCV13) and Recommendations for Use Among Children - Advisory Committee on Immunization Practices (ACIP), 2010. MMWR 2010; 59:260.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
